import React, { useState, useEffect } from "react";
import ItemService from "../services/ItemService";

const Item = props => {
    const initialBrandState = {
        asin: null,
        title: "",
        description: "",
        brand: false
    };
    const [currentBrand, setCurrentBrand] = useState(initialBrandState);
    const [message, setMessage] = useState("");

    const getItemByAsin = asin => {
        ItemService.getItemByAsin(asin)
            .then(response => {
                setCurrentBrand(response.data);
                console.log(response.data);
            })
            .catch(e => {
                console.log(e);
            });
    };

    useEffect(() => {
        getItemByAsin(props.match.params.asin);
    }, [props.match.params.asin]);

    const handleInputChange = event => {
        const { name, value } = event.target;
        setCurrentBrand({ ...currentBrand, [name]: value });
    };


    return (
        <div>
            {currentBrand ? (
                <div className="edit-form">
                    <h4>Brand</h4>
                    <form>
                        <div className="form-group">
                            <label htmlFor="title">Title</label>
                            <input
                                type="text"
                                className="form-control"
                                id="title"
                                name="title"
                                value={currentBrand.title}
                                onChange={handleInputChange}
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="description">Description</label>
                            <input
                                type="text"
                                className="form-control"
                                id="description"
                                name="description"
                                value={currentBrand.description}
                                onChange={handleInputChange}
                            />
                        </div>

                        {/*<div className="form-group">*/}
                        {/*    <label>*/}
                        {/*        <strong>Status:</strong>*/}
                        {/*    </label>*/}
                        {/*    {currentBrand.published ? "Published" : "Pending"}*/}
                        {/*</div>*/}
                    </form>
                </div>
            ) : (
                <div>
                    <br />
                    <p>Please click on an item...</p>
                </div>
            )}
        </div>
    );
};

export default Tutorial;
