import axios from 'axios';

const MUSIC_ITEM_API_BASE_URL = "http://localhost:8081/";


    const getAllItemsByBrand = (brand) => {

        var params = new URLSearchParams();
        params.append("page", 0);
        params.append("size", 5);

        var request = {
            params: params
        };

        axios.get(`http://localhost:8082/getAllItemsByBrand/${brand}`,request).then(function (response) {
            // console.log(response);
            return response.data;
        }).catch(
            function (error) {
                console.log(error)
            }
        );
    }

    const getItemByAsin= (asin) => {
        return axios.get(MUSIC_ITEM_API_BASE_URL + '/getItemByAsin/' + asin);
    }



export default {
    getAllItemsByBrand,
    getItemByAsin
}
