import { useRef,useEffect } from "react";
import useState from 'react-usestateref'

import React from "react";
import ReactDOM from 'react-dom';
import ItemService from '../services/ItemService'
import ItemTable from "./PopulateTable";

    function FetchData(){

        // Render the UI for your table
        const initialBrand = ''
        const [brand, setBrand, brandRef] = useState('');
        useEffect(() => {
            setBrand(inputRef.current.value);
            console.log(brandRef.current) // It will have the last value
        })

        const inputRef = useRef();
        let apiData;

        const submitHandler = (e) => {
            e.preventDefault();

            // setBrand(inputRef.current.value);

            // console.log("brand")
            // console.log(brand);
            // apiData = ItemService.getAllItemsByBrand(inputRef.current.value);
            apiData = ItemService.getAllItemsByBrand(brand);
            // console.log(apiData)
            const p = Promise.resolve(apiData);
            p.then(function(v) {
                const element = <ItemTable brandData={v["Items"]}/>;
                // console.log("inside submitHandler");
                // console.log(element);
                ReactDOM.render(element, document.getElementById('list'))
            });

        };

        return (
            <div className="App">
                <form onSubmit={submitHandler}>
                    <input ref={inputRef} />
                    <button type="submit">Submit</button>
                        <div style={{display: 'flex',  justifyContent:'center', alignItems:'center', height: '40vh'}} id="list"/>
                </form>



            </div>
        );
    };


    export default FetchData
